package basic;

public class mymath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.pow(2, 8));
		System.out.println(Math.abs(-122.29));//overloading becoz changing datatype
		System.out.println(Math.abs(-122));//overloading
		System.out.println(Math.sqrt(27));
		System.out.println(Math.cos(180));
		System.out.println((long)(Math.random()*1000000));
		System.out.println(Math.sin(90));
		System.out.println(Math.PI);
		System.out.println(Math.random()*10);
		System.out.println(Math.random()*100);
		System.out.println(Math.min(7, 9));//overloading 
		System.out.println(Math.min(7.3, 9.78));//overloading
		
		
		
	}

}
