package basic;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 20;

		System.out.println("a ---->" + a + " " + "  b ------>" + b);
		a = a + b;
		b = a - b;
		a = a - b;
		System.out.println("a ---->" + a + " " + "  b ------>" + b);

	}

}
